insert into MBTI.Types(typeuk, typename, typedescription) values(2,'ENTJ','A Commander (ENTJ) is someone with the Extraverted, Intuitive, Thinking, and Judging personality traits. They are decisive people who love momentum and accomplishment. They gather information to construct their creative visions but rarely hesitate for long before acting on them.');                                  
insert into MBTI.Types(typeuk, typename, typedescription) values(3,'INTJ','An Architect (INTJ) is a person with the Introverted, Intuitive, Thinking, and Judging personality traits. These thoughtful tacticians love perfecting the details of life, applying creativity and rationality to everything they do. Their inner world is often a private, complex one.');                                
insert into MBTI.Types(typeuk, typename, typedescription) values(1,'ENTP','A Debater (ENTP) is a person with the Extraverted, Intuitive, Thinking, and Prospecting personality traits. They tend to be bold and creative, deconstructing and rebuilding ideas with great mental agility. They pursue their goals vigorously despite any resistance they might encounter.');                            
insert into MBTI.Types(typeuk, typename, typedescription) values(4,'INTP','A Logician (INTP) is someone with the Introverted, Intuitive, Thinking, and Prospecting personality traits. These flexible thinkers enjoy taking an unconventional approach to many aspects of life. They often seek out unlikely paths, mixing willingness to experiment with personal creativity.');                      
insert into MBTI.Types(typeuk, typename, typedescription) values(5,'ENFP','A Campaigner (ENFP) is someone with the Extraverted, Intuitive, Feeling, and Prospecting personality traits. These people tend to embrace big ideas and actions that reflect their sense of hope and goodwill toward others. Their vibrant energy can flow in many directions.');                                           
insert into MBTI.Types(typeuk, typename, typedescription) values(6,'ENFJ','A Protagonist (ENFJ) is a person with the Extraverted, Intuitive, Feeling, and Judging personality traits. These warm, forthright MBTI.Types love helping others, and they tend to have strong ideas and values. They back their perspective with the creative energy to achieve their goals.');                                 
insert into MBTI.Types(typeuk, typename, typedescription) values(7,'INFP','A Mediator (INFP) is someone who possesses the Introverted, Intuitive, Feeling, and Prospecting personality traits. These rare personality MBTI.Types tend to be quiet, open-minded, and imaginative, and they apply a caring and creative approach to everything they do.');                                                    
insert into MBTI.Types(typeuk, typename, typedescription) values(8,'INFJ','An Advocate (INFJ) is someone with the Introverted, Intuitive, Feeling, and Judging personality traits. They tend to approach life with deep thoughtfulness and imagination. Their inner vision, personal values, and a quiet, principled version of humanism guide them in all things.');                                  
insert into MBTI.Types(typeuk, typename, typedescription) values(9,'ESFJ','A Consul (ESFJ) is a person with the Extraverted, Observant, Feeling, and Judging personality traits. They are attentive and people-focused, and they enjoy taking part in their social community. Their achievements are guided by decisive values, and they willingly offer guidance to others.');                        
insert into MBTI.Types(typeuk, typename, typedescription) values(10,'ESTJ','An Executive (ESTJ) is someone with the Extraverted, Observant, Thinking, and Judging personality traits. They possess great fortitude, emphatically following their own sensible judgment. They often serve as a stabilizing force among others, able to offer solid direction amid adversity.');                         
insert into MBTI.Types(typeuk, typename, typedescription) values(11,'ISFJ','A Defender (ISFJ) is someone with the Introverted, Observant, Feeling, and Judging personality traits. These people tend to be warm and unassuming in their own steady way. They’re efficient and responsible, giving careful attention to practical details in their daily lives.');                                      
insert into MBTI.Types(typeuk, typename, typedescription) values(12,'ISTJ','A Logistician (ISTJ) is someone with the Introverted, Observant, Thinking, and Judging personality traits. These people tend to be reserved yet willful, with a rational outlook on life. They compose their actions carefully and carry them out with methodical purpose.');                                              
insert into MBTI.Types(typeuk, typename, typedescription) values(13,'ESFP','An Entertainer (ESFP) is a person with the Extraverted, Observant, Feeling, and Prospecting personality traits. These people love vibrant experiences, engaging in life eagerly and taking pleasure in discovering the unknown. They can be very social, often encouraging others into shared activities.');               
insert into MBTI.Types(typeuk, typename, typedescription) values(14,'ESTP','An Entrepreneur (ESTP) is someone with the Extraverted, Observant, Thinking, and Prospecting personality traits. They tend to be energetic and action-oriented, deftly navigating whatever is in front of them. They love uncovering life’s opportunities, whether socializing with others or in more personal pursuits.');
insert into MBTI.Types(typeuk, typename, typedescription) values(15,'ISFP','An Adventurer (ISFP) is a person with the Introverted, Observant, Feeling, and Prospecting personality traits. They tend to have open minds, approaching life, new experiences, and people with grounded warmth. Their ability to stay in the moment helps them uncover exciting potentials.');                            
insert into MBTI.Types(typeuk, typename, typedescription) values(16,'ISTP','A Virtuoso (ISTP) is someone with the Introverted, Observant, Thinking, and Prospecting personality traits. They tend to have an individualistic mindset, pursuing goals without needing much external connection. They engage in life with inquisitiveness and personal skill, varying their approach as needed.');       
